sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue == oldValue ) {
		return;
	}
	//Type appropriate comment here, and begin script below
	var user = g_form.getValue('u_requested_for');
	//Make Asynchronous call to Server
	var ga = new GlideAjax('getEmailAddress'); //Create an instance of your class
	ga.addParam('sysparm_name', 'getUserEmail');//Call appropriate method from your class
	ga.addParam('sysparm_user', user);//Send input parameter of your method
	ga.getXML(retrieveEmail);//Make an asynchronous call to your class


	function retrieveEmail(response){//Get XML will return a response
		var answer = response.responseXML.documentElement.getAttribute("answer");//Get the "answer" attribute from your XML response
		g_form.setValue('u_requested_for_email', answer);
	}
}
